      run = this.buffer.getBits(6);
      level = this.buffer.getBits(8);
      if( level == 0 ) {
        level = this.buffer.getBits(8);
      }
      else if( level == 128 ) {
        level = this.buffer.getBits(8) - 256;
      }
      else if( level > 128 ) {
        level = level - 256;
      }
    }
    else {
      run = coeff >> 8;
      level = coeff & 0xff;
      if( this.buffer.getBits(1) ) {
