  else {
    // Add data to the predicted macroblock
    if (n == 1) {
      this.addValueToDestination((this.blockData[0] + 128) >> 8, destArray, destIndex, scan);
      this.blockData[0] = 0;
    } else {
      this.IDCT();
      this.addBlockToDestination(this.blockData, destArray, destIndex, scan);
